import React from 'react';
import SearchForm from './SearchForm';

const pokedex = require('pokedex-promise-v2');
const P = new pokedex();


class App extends React.Component {
  state = {
    pokemon: []
  };

  searchResponse = {};

  modifyState = (response) => {
    this.setState({pokemon: response});
  };

  searchPokemon = (pokemon) => {
    const that = App;
    console.log('pokemon vu', pokemon);
    P.getPokemonByName(pokemon.name)
      .then(function(response) {
        console.log(response);
        that.searchResponse = response;
        console.log('searchResponse ', that.searchResponse);
        //that.modifyState(that.searchResponse);
      })
      .catch(function(error) {
        console.log('There was an error : ', error);
        return error;
      });
      this.modifyState(this.searchResponse);
  }

  render() {
    return (
      <div className="container">
        <h1>PokeFind</h1>
        <h2>Find your Pokemons</h2>
        <SearchForm formTitle="Enter Pokemon's name" searchPokemon={this.searchPokemon}/>
        <p>hello world</p>
      </div>
    );
  }
}


export default App;
