'use strict';

var uniqueRandomArray = require('unique-random-array');

var pokemon = require('./data/en');

var repositoryUrl = 'https://github.com/sindresorhus/pokemon';
var reportText = "Please report to ".concat(repositoryUrl, "/issues if we missed something.");
var languages = new Set(['de', 'en', 'fr', 'ja', 'ko', 'ru', 'zh-Hans', 'zh-Hant']);
var randomNameGenerators = new Map();

function getLocalizedList() {
  var language = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : 'en';

  if (language === 'en') {
    return pokemon;
  }

  if (!languages.has(language)) {
    throw new Error("Localized list for language code '".concat(language, "' does not exist. Pull request welcome: ").concat(repositoryUrl));
  }

  return require("./data/".concat(language.toLowerCase()));
}

exports.all = getLocalizedList;

exports.random = function () {
  var language = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : 'en';

  if (randomNameGenerators.has(language)) {
    return randomNameGenerators.get(language)();
  }

  var list = getLocalizedList(language);
  var random = uniqueRandomArray(list);
  randomNameGenerators.set(language, random);
  return random();
};

exports.getName = function (id) {
  var language = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 'en';
  var list = getLocalizedList(language);
  var name = list[id - 1];

  if (!name) {
    throw new Error("Pok\xE9mon with ID '".concat(id, "' does not exist. ").concat(reportText));
  }

  return name;
};

exports.getId = function (name) {
  var language = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 'en';
  var list = getLocalizedList(language);
  var index = list.indexOf(name);

  if (index === -1) {
    throw new Error("Pok\xE9mon with name '".concat(name, "' does not exist for language code '").concat(language, "'. ").concat(reportText));
  }

  return index + 1;
};

exports.languages = languages;
